pragma solidity ^0.8.4;

import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";

/// @title Semver
/// @notice A simple contract for managing contract versions.
contract Semver {
    // Contract's major version number.
    uint256 private immutable _major;

    // Contract's minor version number.
    uint256 private immutable _minor;

    // Contract's patch version number.
    uint256 private immutable _path;

    /// @dev Create a new Semver instance.
    /// @param major Major version number.
    /// @param minor Minor version number.
    /// @param patch Patch version number.
    constructor(uint256 major, uint256 minor, uint256 patch) {
        _major = major;
        _minor = minor;
        _path = patch;
    }

    /// @notice Returns the full semver contract version.
    /// @return Semver contract version as a string.
    function version() external view returns (string memory) {
        return
            string(
                abi.encodePacked(Strings.toString(_major), ".", Strings.toString(_minor), ".", Strings.toString(_path))
            );
    }
}","keccak256":"0x3c960ee02543c88107d840df240bab23ac07d583ba7ff582c9b5cac1b8b73a7c","license":"MIT"},"contracts/resolver/ISchemaResolver.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import { Attestation } from "../Common.sol";

/// @title ISchemaResolver
/// @notice The interface of an optional schema resolver.
interface ISchemaResolver {
    /// @notice Checks if the resolver can be sent ETH.
    /// @return Whether the resolver supports ETH transfers.
    function isPayable() external pure returns (bool);

    /// @notice Processes an attestation and verifies whether it's valid.
    /// @param attestation The new attestation.
    /// @return Whether the attestation is valid.
    function attest(Attestation calldata attestation) external payable returns (bool);

    /// @notice Processes multiple attestations and verifies whether they are valid.
    /// @param attestations The new attestations.
    /// @param values Explicit ETH amounts which were sent with each attestation.
    /// @return Whether all the attestations are valid.
    function multiAttest(
        Attestation[] calldata attestations,
        uint256[] calldata values
    ) external payable returns (bool);

    /// @notice Processes an attestation revocation and verifies if it can be revoked.
    /// @param attestation The existing attestation to be revoked.
    /// @return Whether the attestation can be revoked.
    function revoke(Attestation calldata attestation) external payable returns (bool);

    /// @notice Processes revocation of multiple attestation and verifies they can be revoked.
    /// @param attestations The existing attestations to be revoked.
    /// @param values Explicit ETH amounts which were sent with each revocation.
    /// @return Whether the attestations can be revoked.
    function multiRevoke(
        Attestation[] calldata attestations,
        uint256[] calldata values
    ) external payable returns (bool);
}","keccak256":"0x1dec285f07614107174b652568474a55b6a2692631888731967fb5cc53d4f417","license":"MIT"},"contracts/resolver/SchemaResolver.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import { ITAS, Attestation } from "../ITAS.sol";
import { AccessDenied, InvalidTAS, InvalidLength, uncheckedInc } from "../Common.sol";
import { Semver } from "../Semver.sol";

import { ISchemaResolver } from "./ISchemaResolver.sol";

/// @title SchemaResolver
/// @notice The base schema resolver contract.
abstract contract SchemaResolver is ISchemaResolver, Semver {
    error InsufficientValue();
    error NotPayable();

    // The global tas contract.
    ITAS internal immutable _tas;

    /// @dev Creates a new resolver.
    /// @param tas The address of the global tas contract.
    constructor(ITAS tas) Semver(0, 0, 1) {
        if (address(tas) == address(0)) {
            revert InvalidTAS();
        }

        _tas = tas;
    }

    /// @dev Ensures that only the tas contract can make this call.
    modifier onlytas() {
        _onlytas();

        _;
    }

    /// @inheritdoc ISchemaResolver
    function isPayable() public pure virtual returns (bool) {
        return false;
    }

    /// @dev ETH callback.
    receive() external payable virtual {
        if (!isPayable()) {
            revert NotPayable();
        }
    }

    /// @inheritdoc ISchemaResolver
    function attest(Attestation calldata attestation) external payable onlytas returns (bool) {
        return onAttest(attestation, msg.value);
    }

    /// @inheritdoc ISchemaResolver
    function multiAttest(
        Attestation[] calldata attestations,
        uint256[] calldata values
    ) external payable onlytas returns (bool) {
        uint256 length = attestations.length;
        if (length != values.length) {
            revert InvalidLength();
        }

        // We are keeping track of the remaining ETH amount that can be sent to resolvers and will keep deducting
        // from it to verify that there isn't any attempt to send too much ETH to resolvers. Pltase note that unless
        // some ETH was stuck in the contract by accident (which shouldn't happen in normal conditions), it won't be
        // possible to send too much ETH anyway.
        uint256 remainingValue = msg.value;

        for (uint256 i = 0; i < length; i = uncheckedInc(i)) {
            // Ensure that the attester/revoker doesn't try to spend more than available.
            uint256 value = values[i];
            if (value > remainingValue) {
                revert InsufficientValue();
            }

            // Forward the attestation to the underlying resolver and return false in case it isn't approved.
            if (!onAttest(attestations[i], value)) {
                return false;
            }

            unchecked {
                // Subtract the ETH amount, that was provided to this attestation, from the global remaining ETH amount.
                remainingValue -= value;
            }
        }

        return true;
    }

    /// @inheritdoc ISchemaResolver
    function revoke(Attestation calldata attestation) external payable onlytas returns (bool) {
        return onRevoke(attestation, msg.value);
    }

    /// @inheritdoc ISchemaResolver
    function multiRevoke(
        Attestation[] calldata attestations,
        uint256[] calldata values
    ) external payable onlytas returns (bool) {
        uint256 length = attestations.length;
        if (length != values.length) {
            revert InvalidLength();
        }

        // We are keeping track of the remaining ETH amount that can be sent to resolvers and will keep deducting
        // from it to verify that there isn't any attempt to send too much ETH to resolvers. Pltase note that unless
        // some ETH was stuck in the contract by accident (which shouldn't happen in normal conditions), it won't be
        // possible to send too much ETH anyway.
        uint256 remainingValue = msg.value;

        for (uint256 i = 0; i < length; i = uncheckedInc(i)) {
            // Ensure that the attester/revoker doesn't try to spend more than available.
            uint256 value = values[i];
            if (value > remainingValue) {
                revert InsufficientValue();
            }

            // Forward the revocation to the underlying resolver and return false in case it isn't approved.
            if (!onRevoke(attestations[i], value)) {
                return false;
            }

            unchecked {
                // Subtract the ETH amount, that was provided to this attestation, from the global remaining ETH amount.
                remainingValue -= value;
            }
        }

        return true;
    }

    /// @notice A resolver callback that should be implemented by child contracts.
    /// @param attestation The new attestation.
    /// @param value An explicit ETH amount that was sent to the resolver. Pltase note that this value is verified in
    ///     both attest() and multiAttest() callbacks tas-only callbacks and that in case of multi attestations, it'll
    ///     usually hold that msg.value != value, since msg.value aggregated the sent ETH amounts for all the
    ///     attestations in the batch.
    /// @return Whether the attestation is valid.
    function onAttest(Attestation calldata attestation, uint256 value) internal virtual returns (bool);

    /// @notice Processes an attestation revocation and verifies if it can be revoked.
    /// @param attestation The existing attestation to be revoked.
    /// @param value An explicit ETH amount that was sent to the resolver. Pltase note that this value is verified in
    ///     both revoke() and multiRevoke() callbacks tas-only callbacks and that in case of multi attestations, it'll
    ///     usually hold that msg.value != value, since msg.value aggregated the sent ETH amounts for all the
    ///     attestations in the batch.
    /// @return Whether the attestation can be revoked.
    function onRevoke(Attestation calldata attestation, uint256 value) internal virtual returns (bool);

    /// @dev Ensures that only the tas contract can make this call.
    function _onlytas() private view {
        if (msg.sender != address(_tas)) {
            revert AccessDenied();
        }
    }
}","keccak256":"0x15b8bc50da941d4cd5ca986efc37e738331b6ddb3f86c0fe953247606fecad42","license":"MIT"},"contracts/resolver/examples/ACResolver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import { TablelandDeployments, ITablelandTables } from "@tableland/evm/contracts/utils/TablelandDeployments.sol";

import { SQLHelpers } from "@tableland/evm/contracts/utils/SQLHelpers.sol";

import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";

import {ITAS, Attestation} from "../../ITAS.sol";

import {SchemaResolver} from "../SchemaResolver.sol";

import "../../ISchemaRegistry.sol";

/**
 * @title ContentSubscriptionResolver
 * @notice A schema resolver subcription to content example
 */

contract ACResolver is SchemaResolver, AccessControl {    
        // State variables
    ISchemaRegistry private schemaRegistry;

    address public splitterFactory;

    struct SchemaInfo{
        bool AllowAllToAttest;
        bool AllowAllToRevoke;
    }

    mapping(bytes32 => SchemaInfo) private schemas;

    ITablelandTables private tablelandContract;

    string[] createTableStatements; 

    string[] public tables;

    uint256[] tableIDs;

    uint256 tablesUpdates;

    uint256 private tablesRowsCounter;

    string private constant ATTESTER_TABLE_PREFIX = "schema_attesters";

    string private constant ATTESTER_SCHEMA = "schemaUID text, attester text";

    string private constant REVOKER_TABLE_PREFIX = "schema_revokers";

    string private constant REVOKER_SCHEMA = "schemaUID text, revoker text";

    constructor(
        ITAS tas,
        address _splitterFactory,
        ISchemaRegistry _schemaRegistry
    )SchemaResolver(tas){

        schemaRegistry = _schemaRegistry;

        splitterFactory = _splitterFactory;
        tablelandContract = TablelandDeployments.get();        

        createTableStatements.push(SQLHelpers.toCreateFromSchema(
            ATTESTER_SCHEMA,
            ATTESTER_TABLE_PREFIX
        ));

        createTableStatements.push(SQLHelpers.toCreateFromSchema(
            REVOKER_SCHEMA,
            REVOKER_TABLE_PREFIX
        ));


        tableIDs = tablelandContract.create(address(this), createTableStatements);

        tables.push(SQLHelpers.toNameFromId(ATTESTER_TABLE_PREFIX, tableIDs[0]));
        tables.push(SQLHelpers.toNameFromId(REVOKER_TABLE_PREFIX, tableIDs[1]));

    }

    function ACSchemaRegistered(
        address[] calldata attesters,
        address[] calldata revokers,
        string calldata schema,
        string calldata schemaName,
        string calldata schemaDescription
    )external{
        // Register the schema and get its UID
        bytes32 schemaUID = schemaRegistry.register(
            schema,
            schemaName,
            schemaDescription,
            ISchemaResolver(address(this)),
            true
        );
        uint attestersSize = attesters.length;
        if(attestersSize == 0){
            schemas[schemaUID].AllowAllToAttest = true;
        }
        uint revokersSize = revokers.length;
        if(revokersSize == 0){
            schemas[schemaUID].AllowAllToRevoke = true;
        }

        SchemaInfoInserted(schemaUID, attesters, revokers);       
    }

    function SchemaInfoInserted(
        bytes32 schemaUID,
        address[] calldata attesters,
        address[] calldata revokers
    ) internal {
        uint256 totalMax = attesters.length > revokers.length? attesters.length : revokers.length;
        // Managing tableland rows limitation.
        if(tablesRowsCounter + totalMax >= 100000){
            renewTables();
        }
        for(uint i =0; i < attesters.length; i++){
            mutate(
                tableIDs[0],
                SQLHelpers.toInsert(
                    ATTESTER_TABLE_PREFIX,
                    tableIDs[0],
                    "schemaUID, attester",
                    string.concat(
                    SQLHelpers.quote(bytes32ToString(schemaUID)),
                    ",",
                    SQLHelpers.quote(Strings.toHexString(attesters[i]))
                    )
                )
            );
            _grantRole(keccak256(abi.encode(schemaUID, "ATTESTER")), attesters[i]);
        }
        for(uint i =0; i < revokers.length; i++){
            mutate(
                tableIDs[1],
                SQLHelpers.toInsert(
                    REVOKER_TABLE_PREFIX,
                    tableIDs[1],
                    "schemaUID, revoker",
                    string.concat(
                    SQLHelpers.quote(bytes32ToString(schemaUID)),
                    ",",
                    SQLHelpers.quote(Strings.toHexString(revokers[i]))
                    )
                )
            );
            _grantRole(keccak256(abi.encode(schemaUID, "REVOKER")), revokers[i]);
        }
        tablesRowsCounter += totalMax;
    }

    /**
    * @dev Handles attestation by validating the attester and bond value.
    * @param attestation The attestation data.
    * @return Boolean indicating the success of the attestation.
    */
    function onAttest(
        Attestation calldata attestation,
        uint256 /* value */
    ) internal override view returns (bool) {
        address attester = attestation.attester;
        bytes32 schemaUID = attestation.schema;
        if(schemas[schemaUID].AllowAllToAttest){
            return true;
        }
        if (hasRole(keccak256(abi.encode(schemaUID, "ATTESTER")), attester)) {
            return true;
        }
        return false;
    }

    /**
    * @dev Checks if an attestation can be revoked based on resolution status and time.
    * @return Boolean indicating whether the attestation can be revoked.
    */
    function onRevoke(
        Attestation calldata attestation,
        uint256 /* value */
    ) internal override view returns (bool) {
        bytes32 schemaUID = attestation.schema;
        if(schemas[schemaUID].AllowAllToRevoke){
            return true;
        }
        if (hasRole(keccak256(abi.encode(schemaUID, "REVOKER")), tx.origin)) {
            return true;
        }
        return false;
    }

    /**
     * @notice Indicates whether the contract is designed to handle incoming payments.
     * @return True, indicating that the contract can accept payments.
     */
    function isPayable() public pure override returns (bool) {
        return false;
    }

    function renewTables()internal{

        tableIDs = tablelandContract.create(address(this), createTableStatements);

        tables.push(SQLHelpers.toNameFromId(ATTESTER_TABLE_PREFIX, tableIDs[0]));
        tables.push(SQLHelpers.toNameFromId(REVOKER_TABLE_PREFIX, tableIDs[1]));

        tablesRowsCounter = 0; 

        tablesUpdates++;
    }

    function bytes32ToString(bytes32 data) public pure returns (string memory) {
        // Fixed buffer size for hexadecimal convertion
        bytes memory converted = new bytes(data.length * 2);

        bytes memory _base = "0123456789abcdef";

        for (uint256 i = 0; i < data.length; i++) {
        converted[i * 2] = _base[uint8(data[i]) / _base.length];
        converted[i * 2 + 1] = _base[uint8(data[i]) % _base.length];
        }

        return string(abi.encodePacked("0x", converted));
    }


    /*
    * @dev Internal function to execute a mutation on a table.
    * @param {uint256} tableId - Table ID.
    * @param {string} statement - Mutation statement.
    */
    function mutate(uint256 tableId, string memory statement) internal {
        tablelandContract.mutate(address(this), tableId, statement);
    }
}
